t;<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">String</span>&gt;(); \n  <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">list</span>.add(<span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"behold\"</span>);\n  <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">list</span>.add(<span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"bend\"</span>);\n  <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">list</span>.add(<span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"bet\"</span>);\n\n  <span class=\"hljs-comment\" style=\"color: rgb(118, 110, 107);\">//Load the list into a hashSet</span>\n  <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">Set</span>&lt;<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">String</span>&gt; <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">set</span> = <span class=\"hljs-literal\" style=\"color: rgb(223, 83, 32);\">new</span> HashSet&lt;<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">String</span>&gt;(<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">list</span>);\n  <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">if</span> (<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">set</span>.contains(<span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"bend\"</span>))\n  {\n    System.out.println(<span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"String found!\"</span>);\n  }</pre>\n<!-- /wp:html -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>Convert string array to a string array list</h4>\n<!-- /wp:heading -->\n\n<!-- wp:html -->\n<pre class=\"hljs\" style=\"display: block; overflow-x: auto; background: rgb(241, 239, 238); color: rgb(104, 97, 94); padding: 0.5em;\">   <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">String</span>[] language = {\"English\", \"Hindi\"};\n   <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">List</span>&lt;<span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">String</span>&gt; langArray = <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">Arrays</span>.asList(language);\n   <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">List</span>&lt;<span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">String</span>&gt; langArray2 = <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">Arrays</span>.asList(<span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"English\"</span>, <span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"Hindi\"</span>);\n        \n   <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">Integer</span>[] numbers = {3,8,2,5,1,4,7,9};\n   <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">List</span>&lt;<span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">Integer</span>&gt; nList = <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">Arrays</span>.asList(numbers);</pre>\n<!-- /wp:html -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>Find the index of an element in an array in Java</h4>\n<!-- /wp:heading -->\n\n<!-- wp:html -->\n<pre class=\"hljs\" style=\"display: block; overflow-x: auto; background: rgb(241, 239, 238); color: rgb(104, 97, 94); padding: 0.5em;\">   Integer[] numbers = {<span class=\"hljs-number\" style=\"color: rgb(223, 83, 32);\">3</span>,<span class=\"hljs-number\" style=\"color: rgb(223, 83, 32);\">8</span>,<span class=\"hljs-number\" style=\"color: rgb(223, 83, 32);\">2</span>,<span class=\"hljs-number\" style=\"color: rgb(223, 83, 32);\">5</span>,<span class=\"hljs-number\" style=\"color: rgb(223, 83, 32);\">1</span>,<span class=\"hljs-number\" style=\"color: rgb(223, 83, 32);\">4</span>,<span class=\"hljs-number\" style=\"color: rgb(223, 83, 32);\">7</span>,<span class=\"hljs-number\" style=\"color: rgb(223, 83, 32);\">9</span>};\n   <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">int</span> <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">position</span> = Arrays.asList(numbers).indexOf(<span class=\"hljs-number\" style=\"color: rgb(223, 83, 32);\">5</span>);\n   System.out.<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">println</span>(<span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"pos = \"</span> + <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">position</span>); \n   Output : <span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\'pos = 3\'</span></pre>\n<!-- /wp:html -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 id=\"mce_2\"> Simplified check if int array contains int </h4>\n<!-- /wp:heading -->\n\n<!-- wp:html -->\n<pre class=\"hljs\" style=\"display: block; overflow-x: auto; background: rgb(241, 239, 238); color: rgb(104, 97, 94); padding: 0.5em;\">    int searchValue = <span class=\"hljs-number\" style=\"color: rgb(223, 83, 32);\">3</span>;\n    Integer[] <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">num</span> = {<span class=\"hljs-number\" style=\"color: rgb(223, 83, 32);\">1</span>, <span class=\"hljs-number\" style=\"color: rgb(223, 83, 32);\">2</span>, <span class=\"hljs-number\" style=\"color: rgb(223, 83, 32);\">3</span>, <span class=\"hljs-number\" style=\"color: rgb(223, 83, 32);\">4</span>, <span class=\"hljs-number\" style=\"color: rgb(223, 83, 32);\">5</span>};\n\n    boolean <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">status</span> = Arrays.asList(<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">num</span>).contains(searchValue);\n    boolean <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">status</span> = ArrayUtils.contains(<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">num</span>, searchValue);\n    boolean <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">status</span> =  Arrays.binarySearch(<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">num</span>, searchValue) &gt;= <span class=\"hljs-number\" style=\"color: rgb(223, 83, 32);\">0</span>;\n\n    <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">if</span>(<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">status</span>) {\n        <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">return</span> <span class=\"hljs-literal\" style=\"color: rgb(223, 83, 32);\">true</span>;\n    }<span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">else</span>{\n        <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">return</span> <span class=\"hljs-literal\" style=\"color: rgb(223, 83, 32);\">false</span>;\n    }</pre>\n<!-- /wp:html -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 id=\"mce_4\">Sort elements of a string array</h4>\n<!-- /wp:heading -->\n\n<!-- wp:html -->\n<pre class=\"hljs\" style=\"display: block; overflow-x: auto; background: rgb(241, 239, 238); color: rgb(104, 97, 94); padding: 0.5em;\">   String[] <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">language</span> = {<span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"English\"</span>, <span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"Hindi\"</span>};\n   <span class=\"hljs-comment\" style=\"color: rgb(118, 110, 107);\">// for ascending</span>\n   Arrays.<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">sort</span>(<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">language</span>);\n   <span class=\"hljs-comment\" style=\"color: rgb(118, 110, 107);\">// for descending</span>\n   Arrays.<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">sort</span>(<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">language</span>, Collections.reverseOrder());</pre>\n<!-- /wp:html -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>Sort elements of a string array list</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Collections.sort() method is also working in main thread.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n<pre class=\"hljs\" style=\"display: block; overflow-x: auto; background: rgb(241, 239, 238); color: rgb(104, 97, 94); padding: 0.5em;\"><span class=\"hljs-comment\" style=\"color: rgb(118, 110, 107);\">//Normal List</span>\n    <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">List</span>&lt;<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">String</span>&gt; <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">list</span> = <span class=\"hljs-literal\" style=\"color: rgb(223, 83, 32);\">new</span> ArrayList&lt;&gt;();\n    Collections.sort(<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">list</span>, <span class=\"hljs-literal\" style=\"color: rgb(223, 83, 32);\">new</span> Comparator&lt;<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">String</span>&gt;() {\n        @Override\n        <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">public</span> int compare(<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">String</span> item, <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">String</span> item2) {\n            <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">Date</span> <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">date</span> = congertToDate(item);\n            <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">Date</span> date2 = congertToDate(item2);\n            <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">return</span> date2.compareTo(<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">date</span>);\n        }\n    });\n    Collections.sort(<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">list</span>, <span class=\"hljs-literal\" style=\"color: rgb(223, 83, 32);\">new</span> Comparator&lt;<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">String</span>&gt;() {\n        @Override\n        <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">public</span> int compare(<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">String</span> item, <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">String</span> item2) {\n            <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">String</span> value = item;\n            <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">String</span> value2 = item2;\n            <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">return</span> value2.compareToIgnoreCase(value);\n        }\n    });\n    Collections.sort(<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">list</span>, <span class=\"hljs-literal\" style=\"color: rgb(223, 83, 32);\">new</span> Comparator&lt;<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">String</span>&gt;() {\n        @Override\n        <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">public</span> int compare(<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">String</span> item, <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">String</span> item2) {\n            <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">Integer</span> value = <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">Integer</span>.parseInt(item);\n            <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">Integer</span> value2 = <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">Integer</span>.parseInt(item2);\n            <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">return</span> value2.compareTo(value);\n        }\n    });\n\n<span class=\"hljs-comment\" style=\"color: rgb(118, 110, 107);\">//Custom model List    </span>\n    <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">private</span> class User{\n        <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">String</span> id;\n        <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">String</span> name;\n    }\n\n    <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">List</span>&lt;User&gt; userList = <span class=\"hljs-literal\" style=\"color: rgb(223, 83, 32);\">new</span> ArrayList&lt;&gt;();\n    Collections.sort(userList, <span class=\"hljs-literal\" style=\"color: rgb(223, 83, 32);\">new</span> Comparator&lt;User&gt;() {\n        @Override\n        <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">public</span> int compare(User item, User item2) {\n            <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">String</span> value = item.name;\n            <span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">String</span> value2 = item2.name;\n            <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">return</span> value2.compareTo(value);\n        }\n    });</pre>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p>In Kotlin</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n<pre class=\"hljs\" style=\"display: block; overflow-x: auto; background: rgb(241, 239, 238); color: rgb(104, 97, 94); padding: 0.5em;\">val <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">user</span>List : User = ArrayList()\n<span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">user</span>List = <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">user</span>List.sortedWith(compareBy({ it.<span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">user</span>List }))</pre>\n<!-- /wp:html -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>Convert an Array to Comma Separated String in Java</h4>\n<!-- /wp:heading -->\n\n<!-- wp:html -->\n<pre class=\"hljs\" style=\"display: block; overflow-x: auto; background: rgb(241, 239, 238); color: rgb(104, 97, 94); padding: 0.5em;\">   List&lt;<span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">String</span>&gt; words = <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">new</span> ArrayList&lt;&gt;();\n   words.<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">add</span>(<span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"Value1\"</span>);\n   words.<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">add</span>(<span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"Value2\"</span>);\n   <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">String</span> joinedWords = TextUtils.<span class=\"hljs-built_in\" style=\"color: rgb(223, 83, 32);\">join</span>(<span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\",\"</span>, words);</pre>\n<!-- /wp:html -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>Best way to split an array list in Java</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Every sub list have “L” no or items</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n<pre class=\"hljs\" style=\"display: block; overflow-x: auto; background: rgb(241, 239, 238); color: rgb(104, 97, 94); padding: 0.5em;\"><span class=\"hljs-comment\" style=\"color: rgb(118, 110, 107);\">/**\n     * <span class=\"hljs-doctag\">@param</span> list to be chopped\n     * <span class=\"hljs-doctag\">@param</span> L no of items in the list\n     * <span class=\"hljs-doctag\">@param</span> &lt;T&gt; dynamic object of list\n     * <span class=\"hljs-doctag\">@return</span> collection of list\n     */</span>\n    <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">private</span> &lt;T&gt; <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">List</span>&lt;<span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">List</span>&lt;T&gt;&gt; choppedList(<span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">List</span>&lt;T&gt; <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">list</span>, <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">final</span> <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">int</span> L) {\n        <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">List</span>&lt;<span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">List</span>&lt;T&gt;&gt; parts = <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">new</span> ArrayList&lt;<span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">List</span>&lt;T&gt;&gt;();\n        <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">final</span> <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">int</span> N = <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">list</span>.size();\n        <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">for</span> (<span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">int</span> i = <span class=\"hljs-number\" style=\"color: rgb(223, 83, 32);\">0</span>; i &lt; N; i += L) {\n            parts.add(<span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">new</span> ArrayList&lt;T&gt;(\n                    <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">list</span>.subList(i, Math.min(N, i + L)))\n            );\n        }\n        <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">return</span> parts;\n    }</pre>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p> List can be divided into “L” no of size </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n<pre class=\"hljs\" style=\"display: block; overflow-x: auto; background: rgb(241, 239, 238); color: rgb(104, 97, 94); padding: 0.5em;\"><span class=\"hljs-comment\" style=\"color: rgb(118, 110, 107);\">/**\n     * <span class=\"hljs-doctag\">@param</span> list to be chopped\n     * <span class=\"hljs-doctag\">@param</span> L list can be be divide into length(L)\n     * <span class=\"hljs-doctag\">@param</span> &lt;T&gt; dynamic object of list\n     * <span class=\"hljs-doctag\">@return</span> collection of list\n     */</span>\n    <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">private</span> &lt;T&gt; <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">List</span>&lt;<span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">List</span>&lt;T&gt;&gt; choppedList(<span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">List</span>&lt;T&gt; <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">list</span>, <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">final</span> <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">int</span> L) {\n        <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">List</span>&lt;<span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">List</span>&lt;T&gt;&gt; parts = <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">new</span> ArrayList&lt;<span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">List</span>&lt;T&gt;&gt;();\n        <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">int</span> from = <span class=\"hljs-number\" style=\"color: rgb(223, 83, 32);\">0</span>;\n        <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">int</span> chunkSize = <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">list</span>.size() /  L;\n        <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">for</span> (<span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">int</span> i = <span class=\"hljs-number\" style=\"color: rgb(223, 83, 32);\">1</span>; i&lt;= L ; i++){\n            <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">if</span>(i== L){\n                parts.add(<span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">list</span>.subList(from, <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">list</span>.size()));\n            }<span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">else</span> {\n                parts.add(<span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">list</span>.subList(from, from + chunkSize));\n                from = i * chunkSize;\n            }\n        }\n        <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">return</span> parts;\n    }</pre>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->','Become a masters in Array','','inherit','closed','closed','','327-revision-v1','','','2019-09-20 13:13:31','2019-09-20 12:13:31','',327,'http://blogs.appsfeature.com/index.php/2019/09/20/327-revision-v1/',0,'revision','',0),(461,1,'2019-10-04 11:12:21','2019-10-04 10:12:21','<!-- wp:heading {\"level\":4} -->\n<h4 id=\"969c\">Kotlin Coroutines: A brief intro.</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Coroutines are a neat new feature of the Kotlin language that allow us to write asynchronous code in a more idiomatic way. — This also means you can write asynchronous code the same way you would normally write synchronous code in your project.<br><br>In order to do asynchronous programming you may have had to use Async Tasks, Threads (which may have been blocking,&nbsp;<em>meh</em>), Callbacks or even RxJava. Using callbacks could have easily ran you into something known as&nbsp;<em>“</em>Callback Hell<em>” — Shoutout to all the JS programmers who’ll read this.&nbsp;</em>And with RxJava, from experience, I noticed that there was quite the learning curve in-order to get an understanding of how to use the various combinators in the case of chaining a few asynchronous calls. </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 id=\"ff2e\">Coroutines in your Android Project</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Kotlin coroutines in Android are very simple to enable in your project. Since they are still in an experimental mode, you may need to also include a few extra steps in your application level&nbsp;<code>build.gradle</code>&nbsp;file.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>To begin add the following library to your&nbsp;<code>build.gradle</code>&nbsp;file dependencies:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n<pre class=\"hljs\" style=\"display: block; overflow-x: auto; background: rgb(241, 239, 238); color: rgb(104, 97, 94); padding: 0.5em;\">dependencies {\n    implementation <span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"org.jetbrains.kotlinx:kotlinx-coroutines-core:1.2.1\"</span>\n    implementation <span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"org.jetbrains.kotlinx:kotlinx-coroutines-android:1.2.1\"</span>\n    <span class=\"hljs-attr\">...</span><span class=\"hljs-attr\">...</span><span class=\"hljs-attr\">...</span>\n}</pre>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p> I have included code snippet of a coroutine in action is as below: </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n<pre class=\"hljs\" style=\"display: block; overflow-x: auto; background: rgb(241, 239, 238); color: rgb(104, 97, 94); padding: 0.5em;\"><span class=\"hljs-class\"><span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">class</span> <span class=\"hljs-title\" style=\"color: rgb(64, 126, 231);\">MainActivity</span> : <span class=\"hljs-type\" style=\"color: rgb(223, 83, 32);\">AppCompatActivity</span></span>() {\n\n    <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">override</span> <span class=\"hljs-function\"><span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">fun</span> <span class=\"hljs-title\" style=\"color: rgb(64, 126, 231);\">onCreate</span><span class=\"hljs-params\" style=\"color: rgb(223, 83, 32);\">(savedInstanceState: <span class=\"hljs-type\" style=\"color: rgb(223, 83, 32);\">Bundle</span>?)</span></span> {\n        <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">super</span>.onCreate(savedInstanceState)\n        setContentView(R.layout.activity_main)\n\n        CoroutineScope(Dispatchers.Main).launch {\n            toast(<span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"Hello it has started\"</span>)\n            delay(<span class=\"hljs-number\" style=\"color: rgb(223, 83, 32);\">10</span>, TimeUnit.SECONDS)\n            textView.text = <span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"I have changed after 10 seconds\"</span>\n        }\n    }\n}</pre>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p>As you can see above we used coroutine builders in order to launch and execute a coroutine from a scope which was non-suspending. Easy right!?</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Breaking down coroutine builders:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li><code>launch</code>&nbsp;— This builder simply launches a new coroutine and returns a reference to it as a&nbsp;<code>Job</code>&nbsp;object which does not have a result. If you intend to block the current thread, instead of launch you may use&nbsp;<code>runBlocking</code>&nbsp;builder instead.</li><li><code>async</code>&nbsp;— This builder launches new coroutine and returns a reference to it as a&nbsp;<code>Deferred</code>&nbsp;type object which may have a result. It is usually used along with&nbsp;<code>await</code>&nbsp;which is a suspending function which can wait for a result without blocking the current thread.</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>And&nbsp;<code>Dispatchers.Main</code>&nbsp;— this isn’t a coroutine builder however we pass it into our&nbsp;<code>launch</code>&nbsp;builder in order to notify the Android system that we will need to update the UI thread at some point during our coroutine execution.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>Example : </h4>\n<!-- /wp:heading -->\n\n<!-- wp:html -->\n<pre class=\"hljs\" style=\"display: block; overflow-x: auto; background: rgb(241, 239, 238); color: rgb(104, 97, 94); padding: 0.5em;\"><span class=\"hljs-class\"><span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">class</span> <span class=\"hljs-title\" style=\"color: rgb(64, 126, 231);\">MainActivity</span> : <span class=\"hljs-type\" style=\"color: rgb(223, 83, 32);\">AppCompatActivity</span></span>() {\n\n    <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">override</span> <span class=\"hljs-function\"><span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">fun</span> <span class=\"hljs-title\" style=\"color: rgb(64, 126, 231);\">onCreate</span><span class=\"hljs-params\" style=\"color: rgb(223, 83, 32);\">(savedInstanceState: <span class=\"hljs-type\" style=\"color: rgb(223, 83, 32);\">Bundle</span>?)</span></span> {\n        <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">super</span>.onCreate(savedInstanceState)\n        setContentView(R.layout.activity_main)\n\n        <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">val</span> service = RetrofitFactory.makeRetrofitService()\n        CoroutineScope(Dispatchers.IO).launch {\n            <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">val</span> response = service.getPosts()\n            withContext(Dispatchers.Main) {\n                <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">try</span> {\n                    <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">if</span> (response.isSuccessful) {\n                        <span class=\"hljs-comment\" style=\"color: rgb(118, 110, 107);\">//Do something with response e.g show to the UI.</span>\n                    } <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">else</span> {\n                        toast(<span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"Error: <span class=\"hljs-subst\">${response.code()}</span>\"</span>)\n                    }\n                } <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">catch</span> (e: HttpException) {\n                    toast(<span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"Exception <span class=\"hljs-subst\">${e.message}</span>\"</span>)\n                } <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">catch</span> (e: Throwable) {\n                    toast(<span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"Ooops: Something else went wrong\"</span>)\n                }\n            }\n        }\n    }\n}</pre>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p>Reference :</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li> <a href=\"https://proandroiddev.com/how-to-make-sense-of-kotlin-coroutines-b666c7151b93\">https://proandroiddev.com/how-to-make-sense-of-kotlin-coroutines-b666c7151b93</a> </li><li> <a href=\"https://medium.com/androiddevelopers/coroutines-on-android-part-iii-real-work-2ba8a2ec2f45\">https://medium.com/androiddevelopers/coroutines-on-android-part-iii-real-work-2ba8a2ec2f45</a> </li><li><a rel=\"noreferrer noopener\" aria-label=\" (opens in a new tab)\" href=\"https://proandroiddev.com/kotlin-coroutines-for-asynchronous-programming-and-more-1892db686b1\" target=\"_blank\">https://proandroiddev.com/kotlin-coroutines-for-asynchronous-programming-and-more-1892db686b1</a></li><li><a href=\"https://medium.com/@marcogomiero/a-journey-from-async-task-to-kotlin-coroutines-735c273d76cb\">https://medium.com/@marcogomiero/a-journey-from-async-task-to-kotlin-coroutines-735c273d76cb</a></li></ul>\n<!-- /wp:list -->','Kotlin Coroutines and Room','','publish','open','open','','kotlin-coroutines-and-room','','','2019-10-07 08:32:18','2019-10-07 07:32:18','',0,'http://blogs.appsfeature.com/?p=461',0,'post','',0),(462,1,'2019-10-04 11:12:21','2019-10-04 10:12:21','<!-- wp:heading {\"level\":4} -->\n<h4 id=\"969c\">Kotlin Coroutines: A brief intro.</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Coroutines are a neat new feature of the Kotlin language that allow us to write asynchronous code in a more idiomatic way. — This also means you can write asynchronous code the same way you would normally write synchronous code in your project.<br><br>In order to do asynchronous programming you may have had to use Async Tasks, Threads (which may have been blocking, <em>meh</em>), Callbacks or even RxJava. Using callbacks could have easily ran you into something known as <em>“</em>Callback Hell<em>” — Shoutout to all the JS programmers who’ll read this. </em>And with RxJava, from experience, I noticed that there was quite the learning curve in-order to get an understanding of how to use the various combinators in the case of chaining a few asynchronous calls. </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4 id=\"ff2e\">Coroutines in your Android Project</h4>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Kotlin coroutines in Android are very simple to enable in your project. Since they are still in an experimental mode, you may need to also include a few extra steps in your application level&nbsp;<code>build.gradle</code>&nbsp;file.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>To begin add the following library to your&nbsp;<code>build.gradle</code>&nbsp;file dependencies:</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n<pre class=\"hljs\" style=\"display: block; overflow-x: auto; background: rgb(241, 239, 238); color: rgb(104, 97, 94); padding: 0.5em;\">dependencies {\n    implementation <span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"org.jetbrains.kotlinx:kotlinx-coroutines-core:1.2.1\"</span>\n    implementation <span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"org.jetbrains.kotlinx:kotlinx-coroutines-android:1.2.1\"</span>\n    <span class=\"hljs-attr\">...</span><span class=\"hljs-attr\">...</span><span class=\"hljs-attr\">...</span>\n}</pre>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p> I have included code snippet of a coroutine in action is as below: </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n<pre class=\"hljs\" style=\"display: block; overflow-x: auto; background: rgb(241, 239, 238); color: rgb(104, 97, 94); padding: 0.5em;\"><span class=\"hljs-class\"><span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">class</span> <span class=\"hljs-title\" style=\"color: rgb(64, 126, 231);\">MainActivity</span> : <span class=\"hljs-type\" style=\"color: rgb(223, 83, 32);\">AppCompatActivity</span></span>() {\n\n    <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">override</span> <span class=\"hljs-function\"><span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">fun</span> <span class=\"hljs-title\" style=\"color: rgb(64, 126, 231);\">onCreate</span><span class=\"hljs-params\" style=\"color: rgb(223, 83, 32);\">(savedInstanceState: <span class=\"hljs-type\" style=\"color: rgb(223, 83, 32);\">Bundle</span>?)</span></span> {\n        <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">super</span>.onCreate(savedInstanceState)\n        setContentView(R.layout.activity_main)\n\n        CoroutineScope(Dispatchers.Main).launch {\n            toast(<span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"Hello it has started\"</span>)\n            delay(<span class=\"hljs-number\" style=\"color: rgb(223, 83, 32);\">10</span>, TimeUnit.SECONDS)\n            textView.text = <span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"I have changed after 10 seconds\"</span>\n        }\n    }\n}</pre>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p>As you can see above we used coroutine builders in order to launch and execute a coroutine from a scope which was non-suspending. Easy right!?</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Breaking down coroutine builders:</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:list -->\n<ul><li><code>launch</code>&nbsp;— This builder simply launches a new coroutine and returns a reference to it as a&nbsp;<code>Job</code>&nbsp;object which does not have a result. If you intend to block the current thread, instead of launch you may use&nbsp;<code>runBlocking</code>&nbsp;builder instead.</li><li><code>async</code>&nbsp;— This builder launches new coroutine and returns a reference to it as a&nbsp;<code>Deferred</code>&nbsp;type object which may have a result. It is usually used along with&nbsp;<code>await</code>&nbsp;which is a suspending function which can wait for a result without blocking the current thread.</li></ul>\n<!-- /wp:list -->\n\n<!-- wp:paragraph -->\n<p>And <code>Dispatchers.Main</code> — this isn’t a coroutine builder however we pass it into our <code>launch</code> builder in order to notify the Android system that we will need to update the UI thread at some point during our coroutine execution.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":4} -->\n<h4>Example : </h4>\n<!-- /wp:heading -->\n\n<!-- wp:html -->\n<pre class=\"hljs\" style=\"display: block; overflow-x: auto; background: rgb(241, 239, 238); color: rgb(104, 97, 94); padding: 0.5em;\"><span class=\"hljs-class\"><span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">class</span> <span class=\"hljs-title\" style=\"color: rgb(64, 126, 231);\">MainActivity</span> : <span class=\"hljs-type\" style=\"color: rgb(223, 83, 32);\">AppCompatActivity</span></span>() {\n\n    <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">override</span> <span class=\"hljs-function\"><span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">fun</span> <span class=\"hljs-title\" style=\"color: rgb(64, 126, 231);\">onCreate</span><span class=\"hljs-params\" style=\"color: rgb(223, 83, 32);\">(savedInstanceState: <span class=\"hljs-type\" style=\"color: rgb(223, 83, 32);\">Bundle</span>?)</span></span> {\n        <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">super</span>.onCreate(savedInstanceState)\n        setContentView(R.layout.activity_main)\n\n        <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">val</span> service = RetrofitFactory.makeRetrofitService()\n        CoroutineScope(Dispatchers.IO).launch {\n            <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">val</span> response = service.getPosts()\n            withContext(Dispatchers.Main) {\n                <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">try</span> {\n                    <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">if</span> (response.isSuccessful) {\n                        <span class=\"hljs-comment\" style=\"color: rgb(118, 110, 107);\">//Do something with response e.g show to the UI.</span>\n                    } <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">else</span> {\n                        toast(<span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"Error: <span class=\"hljs-subst\">${response.code()}</span>\"</span>)\n                    }\n                } <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">catch</span> (e: HttpException) {\n                    toast(<span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"Exception <span class=\"hljs-subst\">${e.message}</span>\"</span>)\n                } <span class=\"hljs-keyword\" style=\"color: rgb(102, 102, 234);\">catch</span> (e: Throwable) {\n                    toast(<span class=\"hljs-string\" style=\"color: rgb(123, 151, 38);\">\"Ooops: Something else went wrong\"</span>)\n                }\n            }\n        }\n    }\n}</pre>\n<!-- /wp:html -->','Kotlin Coroutines and Room','','inherit','closed','closed','','461-revision-v1','','','2019-10-04 11:12:21','2019-10-04 10:12:21','',461,'http://blogs.appsfeature.com/index.php/2019/10/04/461-revision-v1/',0,'revision','',0),(463,1,'2019-1